INSERT INTO oracle_interpretations (card_id, card_name, orientation, scenario_key, position_key, interpretation) VALUES (1, '魔術師', 'upright', 'general_direction', 'single', '【方位：前進】\n\n看啊，魔術師高舉權杖指向蒼穹，四元素聖器已在他掌間齊備。這並非遲疑的時刻，而是將無形化為有形的創造之始。你內在的潛能正如那無限符號般循環不息，只待你以意志為引，將靈感鑄成現實的道路。前方的路途已由宇宙鋪設，你的每一步都將喚醒沉睡於指尖的星辰。莫再觀望，此刻正是展現力量、向世界宣告你存在的神聖時機——邁步吧，讓魔術師的火焰為你的足跡鍍上金光。') ON CONFLICT (card_id, orientation, scenario_key, position_key) DO UPDATE SET interpretation = EXCLUDED.interpretation;
