INSERT INTO oracle_interpretations (card_id, card_name, orientation, scenario_key, position_key, interpretation) VALUES (1, '魔術師', 'reversed', 'general_direction', 'single', '【方位：留在原地】\n\n逆位的魔術師，其指尖的星辰已然黯淡，四元素的法器散落一地。這並非行動的時刻，而是重新校準內在羅盤的契機。你看，那原本連結天地的姿態已然傾斜，象徵創造力的權杖指向大地而非蒼穹。此刻若盲目追尋方位，只會讓散落的能量更加混亂。請你靜立於此，如同煉金術士在混沌中等待物質沉澱。讓逆位的魔術師成為你的鏡子：真正的方向不在外求，而在你重新拾起地上散落的寶劍、聖杯、錢幣與權杖——那便是你遺忘的意志、情感、物質與靈性之力。待你將四元素重新整合於心，魔術師的斗篷自會再度揚起，為你顯現應許之路。') ON CONFLICT (card_id, orientation, scenario_key, position_key) DO UPDATE SET interpretation = EXCLUDED.interpretation;
