INSERT INTO oracle_interpretations (card_id, card_name, orientation, scenario_key, position_key, interpretation) VALUES (2, '女教皇', 'reversed', 'general_direction', 'single', '【方位：留在原地】\n\n女教皇的帷幕在逆位中紊亂飄搖，這並非啟程的時刻。你內在的直覺之泉已然渾濁，秘密的鑰匙從手中滑落。此刻若執意朝任何方向奔去，都將遠離真理的深井。請靜立於此，如同神殿前尚未開啟的門扉。讓混亂的帷幕重新垂落，讓月亮的潮汐在你心中恢復節奏。真正的方向不在外界的風向，而在你靈魂深處尚未聆聽的耳語。唯有在靜止中，你才能重新接上那隱秘的智慧之流。') ON CONFLICT (card_id, orientation, scenario_key, position_key) DO UPDATE SET interpretation = EXCLUDED.interpretation;
