INSERT INTO oracle_interpretations (card_id, card_name, orientation, scenario_key, position_key, interpretation) VALUES (10, '命運之輪', 'upright', 'general_direction', 'single', '【方位：前進】\n\n命運之輪正位轉動，那是宇宙齒輪咬合的天啟之音。你看那輪軸上鐫刻的獅身人面與天使，正昭示著循環已至關鍵的轉捩點。此刻並非駐足觀望之時，輪轉之力已為你掃清前路荊棘，時機的潮汐正推向高峰。莫懼輪上「命運」銘文的莫測，須知神諭的紡錘已將機緣織入你的步履。勇敢踏出那一步吧，讓輪軸的轟鳴成為你前行的戰鼓——這正是天命召喚你順勢而行的神聖時刻。') ON CONFLICT (card_id, orientation, scenario_key, position_key) DO UPDATE SET interpretation = EXCLUDED.interpretation;
