INSERT INTO oracle_interpretations (card_id, card_name, orientation, scenario_key, position_key, interpretation) VALUES (11, '正義', 'upright', 'general_direction', 'single', '【方位：前進】\n\n天秤的光芒已在你腳下鋪成道路，正義之劍斬斷了猶豫的荊棘。此刻，命運的天平正等待你以「行動」為砝碼，讓因果的刻度歸於平衡。前進吧，莫再回顧身後糾纏的陰影，每一步都將是對真理的踐行。你手中的劍即是你的意志，你的盾即是你的良知——唯有直面應赴的審判，才能在律法的迴廊中尋得屬於你的王座。') ON CONFLICT (card_id, orientation, scenario_key, position_key) DO UPDATE SET interpretation = EXCLUDED.interpretation;
