INSERT INTO oracle_interpretations (card_id, card_name, orientation, scenario_key, position_key, interpretation) VALUES (16, '高塔', 'reversed', 'general_direction', 'single', '【方位：留在原地】\n\n高塔傾頹的雷火已轉為地底深處的悶響，逆位的牌象徵崩塌的延遲而非消弭。此刻若倉皇移動，反會觸動那根早已腐朽的支柱。請像被閃電劈過的橡樹般扎根——你的傷痕正是最穩固的錨點。留在原地不是停滯，而是用指尖觸摸裂縫中滲出的光：那些你曾視為囚牢的結構，其瓦礫下埋著未被雷火焚盡的真理種籽。待你學會從廢墟裡辨識星辰的排列，新的道路自會從你佇立之處生長。') ON CONFLICT (card_id, orientation, scenario_key, position_key) DO UPDATE SET interpretation = EXCLUDED.interpretation;
